import { Component } from '@angular/core';
import {MOVIES} from "./mock-Movies";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Movie Search';
  movies = MOVIES;
  display = false;
  show(search:string){
    if(search === 'Star Wars'){
      this.display=true;
    } else{
      this.display=false;
    }
  }
}
<div>
  <h1>
    {{title}}
  </h1>
  <p>Enter the name of a movie and click submit to see a list of results </p>
    <input #search
           (keyup.enter)="show(search.value)"
           (blur)="show(search.value)">
  <button type="button" (click)="show(search.value)">
      Search
    </button>
  <div *ngIf="display">
    <ul>
      <li *ngFor="let movie of movies">
        {{movie.name}}
      </li>
    </ul>
  </div>
</div>
