import {HttpClient, HttpHeaders} from '@angular/common/http';
import {Observable} from 'rxjs';
import {SearchMovieResponse} from "../interfaces/Search";

@Injectable({
  providedIn: 'root'
})
export class TMDBService {
  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' })
  };
  nodeEndpoint = 'http://localhost:3000/';
  constructor(private httpClient: HttpClient) { }
  getMovieSearchResults(search: string): Observable<any> {
    return this.httpClient.post(this.nodeEndpoint, JSON.stringify(mosearch), this.httpOptions);
