import { Component } from '@angular/core';
import {TMDBService} from "../services/tmdb.service";
import {FormControl, Validators} from "@angular/forms";
import {SearchMovieResponse} from "../interfaces/Search";

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {
  search: string= '';

  searchResults: SearchMovieResponse | undefined
  constructor(private tmdbService: TMDBService,) { }

  searchControl: FormControl = new FormControl('',
    [Validators.required, Validators.minLength(1)]);

  getSearchResults(){
    this.search = this.searchControl.value;
    console.log(`${this.search}`)
    this.tmdbService.getMovieSearchResults(this.search).subscribe(response => {
      this.searchResults = response;
      console.log(`Results: ${this.searchResults}`)
    })
  }
}
<body>
Enter the name of a movie and click submit to see a list of results
<hr/>
Movie: <input  [formControl]="searchControl"><br/>
<button (click)="getSearchResults()">Submit</button>
<div *ngIf="searchControl.invalid">
  Show Error Message
</div>
<app-data [searchResults]="searchResults"></app-data>
</body>
